import time
decor = { "reset": '\033[0m',"bold": '\033[01m',"disable": '\033[02m',"underline": '\033[04m',"reverse": '\033[07m',"strikethrough": '\033[09m',"invisible": '\033[08m',};text = { "black": '\033[30m',"red": '\033[31m',"green": '\033[32m',"orange": '\033[33m',"blue": '\033[34m',"purple": '\033[35m',"cyan": '\033[36m',"lightgrey": '\033[37m',"darkgrey": '\033[90m',"lightred": '\033[91m',"lightgreen": '\033[92m',"yellow": '\033[93m',"lightblue": '\033[94m',"pink": '\033[95m',"lightcyan": '\033[96m',};bg = {"black":     '\033[40m',"red":       '\033[41m',"green":     '\033[42m',"orange":    '\033[43m',"blue":      '\033[44m',"purple":    '\033[45m',"cyan":      '\033[46m',"lightgrey": '\033[47m'}

def start_quiz(quizzez):
    """ Takes as parameter array of quizez: quiz = {"question":"string","question_description":"string","answers":[]}"""

    style = { "question": decor["reset"] + decor["underline"] + decor["bold"] + text["yellow"],"question_description": decor["reset"] +  decor["bold"] + text["purple"],"answers": decor["reset"] + text["red"] + decor["bold"],"pre_input": decor["reset"] + decor["bold"],  "input": decor["reset"] + decor["bold"] + text["blue"],"indicies": decor["reset"] + decor["bold"] + bg["purple"],"incorrect_answer": decor["reset"] + decor["bold"] + text["red"],"correct_answer": decor["reset"] + decor["bold"] + text["green"],"total":decor["reset"] + decor["bold"] + text["yellow"],"skipped": decor["reset"] + decor["bold"] + text["blue"],"time": decor["reset"] + decor["bold"] + text["cyan"],"results": decor["reset"] + decor["bold"] + text["cyan"] + bg["lightgrey"]}
    state = {
        "correct_answers":   0,    #  counter of correct answers 
        "incorrect_answers": 0,    #  counter of incorrect answers 
        "skipped_answers":   0,    #  counter of skipped answers
        "skip_word":         "ЗУПИНИТИСЬ", #  word to stop the quiz
        "begin_time":        None, #  time when test started
        "end_time":          None, #  time when test completed 
        "mistakes":          [],   #  mistakes taked during current test  
        "review":            []    #  questions to review during next test 
    }
    
    
    def styled_question_string(current_quiz): return style["question_description"] + current_quiz["question_description"]+ style["question"] + current_quiz["question"] + decor["reset"]
    def styled_input_string(): return style["pre_input"] + "Відповідь: " + style["input"]
    def styled_incorrect_answer(string): return style["incorrect_answer"] + string
    def styled_correct_answer(string): return style["correct_answer"] + string
    
    print(style["total"] + f"Напишіть у відповідь {style['incorrect_answer'] + state['skip_word'] + style['total']} щоб зупинити тест та побачити результати. ")
    user_answer = None;state["begin_time"] = time.time()

    # iterate trought all quizez
    for i, quiz in enumerate(quizzez):
        # print question with question description 
        print(style["indicies"] + f"{i+1:03d}/{len(quizzez):03d}" + decor["reset"] + " -> " + styled_question_string(quiz))
        user_answer = input(styled_input_string()) # ask user for answer 
        if user_answer in quiz["answers"]: # if answer is correct 
            state["correct_answers"] += 1
        elif user_answer == state["skip_word"]: # if user skipped to the end 
            state["skipped_answers"] += len(quizzez) - i; break
        elif user_answer.strip() == "": # if there is no answer 
            state["skipped_answers"] += 1 
        else: # if answer is wrong 
            state["incorrect_answers"] += 1
            state["review"].append(quiz)    #  add quiz to review, so we can take it later 
            mistake = styled_incorrect_answer(user_answer) + decor["reset"] + decor["bold"] + " -> " + styled_correct_answer(", ".join(quiz["answers"]))
            state["mistakes"].append( mistake )
            print(" > " + mistake)
            
    state["end_time"] = time.time()
    
    # Results! 
    print("\n"
       + style["results"] + "* * Результати * *" + decor["reset"]
       + "\n" + style["correct_answer"]   + "|> Правильно:   "  + str(state["correct_answers"])
       + "\n" + style["incorrect_answer"] + "|> Неправильно: "  + str(state["incorrect_answers"])
       + "\n" + style["total"]            + "|> Загалом:     "  + str(state["correct_answers"] + state["incorrect_answers"])
       + "\n" + style["skipped"]          + "|> Пропущено:   "  + str(state["skipped_answers"])
       + "\n" + style["time"]             + "|> Час:         "  + str(round(state["end_time"] - state["begin_time"], 2)) + " c."
    )
    
    if state["incorrect_answers"] > 0:
        if input(style["pre_input"] + "Показати помилки? так/ні: " + style["input"]) == "так": print("\n".join(state["mistakes"]))
        if input(style["pre_input"] + "Пройти тест заснований на помилках? так/ні: " + style["input"]) == "так": start_quiz(state["review"])
        else: print(decor["reset"])

def removeDoubleLetters(string):
    string = list(string)
    for i in range(len(string)): 
        if string[i] == string[i-1]: string[i] = ""
    return "".join(string)

